#I&rsquo;m creating a very very simple high card wins poker game import random as rand deck = [&lsquo;2&rsquo;,&lsquo;3&rsquo;,&lsquo;4&rsquo;,&lsquo;5&rsquo;,&lsquo;6&rsquo;,&lsquo;7&rsquo;,&lsquo;8&rsquo;,&lsquo;9&rsquo;,&lsquo;10&rsquo;,&lsquo;J&rsquo;,&lsquo;Q&rsquo;,&lsquo;K&rsquo;,&lsquo;A&rsquo;] card_storage = [] hand_storage = [] for hnd in range(0,10): card_storage.append(rand.choice(deck)) hand_storage = [[card_storage[0],card_storage[1],card_storage[2], card_storage[3],card_storage[4]],[card_storage[5],card_storage[6], card_storage[7],card_storage[8],card_storage[9]]]
print(hand_storage)
x = deck.index(card_storage[0])+deck.index(card_storage[1])+deck.index(card_storage[2])+deck.index(card_storage[3])+deck.index(card_storage[4]) y = deck.index(card_storage[5])+deck.index(card_storage[6])+deck.index(card_storage[7])+deck.index(card_storage[8])+deck.index(card_storage[9])
if x &gt; y: print(&lsquo;Player 1 wins&rsquo;) else: print(&lsquo;Player 2 wins&rsquo;)
